mapper.select:
select  mail_senddate 
from email
where MAIL_NUM=#{param1}

mapper.update:
update email
set rcv=0
where mail_num=#{param1}

update email
set send=0
where mail_num=#{param1}

update email
set send=1
where mail_num=#{param1}

update email
set rcv=1
where mail_num=#{param1}

update email
set rcv=2
where mail_num=#{param1}

update email
set send=2
where mail_num=#{param1}

update email
set rcv=-1
where mail_num=#{param1}

update email
set send=-1
where mail_num=#{param1}

mapper.select:
select count(*)
from member
where member_id = #{param1}
